package extern_test

import (
	"testing"

	"github.com/goretk/gore/extern"
	"github.com/stretchr/testify/assert"
)

func TestParseBuildInfo(t *testing.T) {
	a := assert.New(t)
	data := []byte{0x30, 0x77, 0xaf, 0x0c, 0x92, 0x74, 0x08, 0x02, 0x41, 0xe1, 0xc1, 0x07, 0xe6, 0xd6, 0x18, 0xe6, 0x70, 0x61, 0x74, 0x68, 0x09, 0x63, 0x6f, 0x6d, 0x6d, 0x61, 0x6e, 0x64, 0x2d, 0x6c, 0x69, 0x6e, 0x65, 0x2d, 0x61, 0x72, 0x67, 0x75, 0x6d, 0x65, 0x6e, 0x74, 0x73, 0x0a, 0x6d, 0x6f, 0x64, 0x09, 0x63, 0x6f, 0x6d, 0x6d, 0x61, 0x6e, 0x64, 0x2d, 0x6c, 0x69, 0x6e, 0x65, 0x2d, 0x61, 0x72, 0x67, 0x75, 0x6d, 0x65, 0x6e, 0x74, 0x73, 0x09, 0x28, 0x64, 0x65, 0x76, 0x65, 0x6c, 0x29, 0x09, 0x0a, 0xf9, 0x32, 0x43, 0x31, 0x86, 0x18, 0x20, 0x72, 0x00, 0x82, 0x42, 0x10, 0x41, 0x16, 0xd8, 0xf2}

	info, success := extern.ParseBuildInfo(string(data))
	a.True(success)
	a.Equal("command-line-arguments", info.Path)
	a.Equal("(devel)", info.Main.Version)
}
